.big-hero-six {
  display: -ms-flexbox;
  display: flex;
  position: relative;
  height: 800px; /* +200px for the overflow on the top rubber band */
  overflow-y: hidden;
  top: -200px;
  margin-bottom: -200px;
  overflow-x: hidden;
  z-index: 1;
}

  .big-hero-six p {
    margin: 0;
  }

  .big-hero-six p:first-of-type {
      position: relative;
      bottom: -10px;
      left: 3px;
    }

  .big-hero-six p:last-of-type {
      position: relative;
      left: 3px;
    }

  @media (minx-width: 520px) {

  .big-hero-six p:last-of-type {
        top: 8px
    }
      }

  .big-hero-six .background {
    height: 700px;
    width: 100vw;
    text-align: center;
  }

  .big-hero-six .main-content-wrapper {
    padding-top: 100px;
    -ms-flex-align: center;
        align-items: center;
    -ms-flex-pack: start;
        justify-content: flex-start;
  }

  .big-hero-six .background {
    background: #fff;
  }

  .big-hero-six .background img {
      display: none;
      position: absolute;
      height: 100%;
      right: -40px;
      }

  .big-hero-six video {
    position: relative;
    height: 110%;
    right: -40px;
  }

.memoji {
  display: none;
  height: 60px;
  margin-right: 10px;
}

#unecessary-part-of-name {
  -webkit-animation: 10s ease-in-out 3s fade-away;
          animation: 10s ease-in-out 3s fade-away;
  -webkit-animation-fill-mode: forwards;
          animation-fill-mode: forwards;
}

@-webkit-keyframes fade-away {
  from { opacity: 1; }
  to { opacity: 0.1; }
}

@keyframes fade-away {
  from { opacity: 1; }
  to { opacity: 0.1; }
}

/* Some switcharoos on what to show on the hero, depending on the size */

@media (max-width: 1112px) {
  .big-hero-six video {
    display: none;
  }
  .big-hero-six .background img {
    display: block;
  }
}

@media (max-width: 780px) {
  .big-hero-six .background img {
    display: none;
  }

  .memoji {
      display: block;
  }
}

@media (max-width: 624px) {
  .big-hero-six h1 {
    font-size: 11vw;
  }
}

@media (max-width: 520px) {
  .big-hero-six .main-content-wrapper {
    -ms-flex-align: center;
        align-items: center;
    -ms-flex-pack: start;
        justify-content: flex-start;
    font-size: 16px;
  }

  .memoji {
    height: 44px;
    margin-right: 10px;
  }

  .big-hero-six h1 {
    font-size: 42px;
    margin:4px 0 4px 0;
  }
}
